import React, {useEffect} from 'react';
import Routes from './routes';
import Routes2 from './routes2';

function App() {

  function handleTopo() {
    if(window.scrollY > 200) {
      console.log('open');
    } else {
      console.log('close');
    }
  }

  useEffect(() => {
    let subscribe = true;
    if(subscribe) {
      window.onscroll = () => handleTopo();
    }
    return () => {
      subscribe = false;
      console.log('unMount')
    }

  }, [])



  return(
    <div>
      <h1>Welcome to Next.js!</h1>
    <Routes />
    </div>
  );
}

export default App;
