import React, {useState, useEffect} from 'react';

function Home() {

function handleTopo() {
  if(window.scrollY > 200) {
    console.log('open');
  } else {
    console.log('close');
  }
}

useEffect(() => {
  let subscribe = true;
  console.log('DidMount')
  if(subscribe) {
    window.addEventListener('scroll', handleTopo)
  }
  return () => {
    subscribe = false;
    console.log('WillUnMount')
    window.removeEventListener('scroll', handleTopo)
  }
}, [])

  return (
    <>
        <h1>Hello World</h1>
    </>
);
}



export default Home;
